import React, { useEffect, useState } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner';
import PropTypes from 'prop-types'
import InfiniteScroll from "react-infinite-scroll-component";

const News = (props) => {
    const [articles, setArticles] = useState([])
    const [loading, setLoading] = useState(true)
    const [page, setPage] = useState(1)
    const [totalResults, setTotalResults] = useState(0)

    const capitalizeFirstLetter = (string) => {
        return string.charAt(0).toUpperCase() + string.slice(1);
    }



    const fetchNews = async (pageNumber = 1) => {
        props.setProgress(10);
        const url = "https://google.serper.dev/news";
        const apiKey = process.env.REACT_APP_SERPER_API;


        if (!apiKey) {
            console.error("API key is missing. Please check your .env file.");
            return;
        }


        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "X-API-KEY": apiKey
            },
            body: JSON.stringify({
                q: props.category, num: props.pageSize, // Number of results per request
                page: pageNumber,
            }) // Search by category
        };

        setLoading(true);
        try {
            const response = await fetch(url, options);
            props.setProgress(30);
            const data = await response.json();
            props.setProgress(70);

            if (data.news) {
                setArticles((prevArticles) =>
                    pageNumber === 1 ? data.news : [...prevArticles, ...data.news]
                );
                setTotalResults(data.news.length);
                setPage(pageNumber);
            }

        } catch (error) {
            console.error("Error fetching news:", error);
        }
        setLoading(false);
        props.setProgress(100);
    };



    // const updateNews = async ()=> {
    //     props.setProgress(10);
    //     const url = `https://google.serper.dev/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${page}&pageSize=${props.pageSize}`; 
    //     setLoading(true)
    //     let data = await fetch(url);
    //     props.setProgress(30);
    //     let parsedData = await data.json()
    //     props.setProgress(70);
    //     setArticles(parsedData.articles)
    //     setTotalResults(parsedData.totalResults)
    //     setLoading(false)
    //     props.setProgress(100);
    // }




    useEffect(() => {
        document.title = `${capitalizeFirstLetter(props.category)} - NewsMonkey`;
        // updateNews(); 
        // eslint-disable-next-line
        fetchNews(1);
    }, [props.category])


    const fetchMoreData = async () => {
        // setPage(page + 1);
        await fetchNews(page + 1);
    };

    // const fetchMoreData = async () => {   
    //     const url = `https://google.serper.dev/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${page+1}&pageSize=${props.pageSize}`;
    //     setPage(page+1) 
    //     let data = await fetch(url);
    //     let parsedData = await data.json()
    //     setArticles(articles.concat(parsedData.articles))
    //     setTotalResults(parsedData.totalResults)
    //   };

    return (
        <>
            <h1 className="text-center" style={{ margin: '35px 0px', marginTop: '90px' }}>NewsMonkey - Top {capitalizeFirstLetter(props.category)} Headlines</h1>
            {loading && <Spinner />}
            <InfiniteScroll
                dataLength={articles.length}
                next={fetchMoreData}
                hasMore={articles.length !== totalResults}
                loader={<Spinner />}
            >
                <div className="container">

                    <div className="row">
                        {articles.map((element) => {
                            return <div className="col-md-4" key={element.url}>
                                <NewsItem title={element.title ? element.title : ""} description={element.description ? element.description : ""} imageUrl={element.urlToImage} newsUrl={element.url} author={element.author} date={element.publishedAt} source={element.source.name} />
                            </div>
                        })}
                    </div>
                </div>
            </InfiniteScroll>
        </>
    )

}


News.defaultProps = {
    country: 'in',
    pageSize: 8,
    category: 'general',
}

News.propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
    setProgress: PropTypes.func.isRequired

}

export default News
